from PIL import Imagefrom tkinter import Tkfrom tkinter.filedialog import askopenfilenameimport osTk().withdraw()file_path  = askopenfilename()print(file_path)old = Image.open(file_path)def png(image, name_input):    png_format = old.save(file_path)    image.save(f"/Users/asmaarabid/Downloads/{name_input}.png", format="png")def jpg(image, name_input):    svg_format = old.save(file_path)    image.save(f"/Users/asmaarabid/Downloads/{name_input}.jpg", format="jpg")    def option_1():    print("Select the Image you want to change the format for ")    format_choice = int(input("Enter a choice (1-2): "))        if format_choice == 1:        name_input = input("Enter name for file : ")        print(f"You changed {file_path} to a .PNG format")        print(f"Named {name_input}")        png(old, name_input)        elif format_choice == 2:        name_input = input("Enter name for file: ")        print(f"You changed {file_path} to a .jpg format")        print(f"Named {name_input}")        png(old,  name_input)            else:        print("Unsupported Format")    def option_2():    try:        width = int(input("Enter new width: "))        height = int(input("Enter new height: "))        filename = input("Enter name for resized file: ")                original_format = old.format        orginal_extension = os.path.splitext((file_path))[1]        re_image = old.resize((width, height))                save_path = (f"/Users/asmaarabid/Downloads/{filename}{orginal_extension}{orginal_extension}")        re_image.save(save_path, format=original_format)                print(f"Your image is now resized to {width}x{height} and saved as {filename}.")    except ValueError:        print("Please enter valid numbers for width and height.")def main_menu():    print("Welcome to Image Converter!")        print("\n1. Convert image to format")    print("2. Change image size")        while True:                choice=int(input("Enter your choice(1-2): "))                if choice ==1:            print("Enter a format \n1. .png \n2. .jpg")            option_1()            break                elif choice ==2:            option_2()            breakif __name__ == "__main__":    main_menu()